				$(element).wrap('<div class="sk_mask"></div>');
				// set shortcut handle to mask
				mask = $('#'+frameId+' .sk_mask');
				
				mask.css({
					'position'	: 'absolute',
					'display'	: 'inline-block',
					'top' 		: padding,
					'bottom'	: padding,
					'right' 	: padding,
					'left'		: padding,
					'overflow'	: 'hidden'
				});
				
